import React from 'react';
import './App.css';

const data = require('./teams.json') as {teams:Team[]}
interface Team {
  school: string;
  name: string;
  city: string;
  state: string;
}
const allTeams = data.teams;

function NameHeader() {
  return (
    <div>
      <h1>Tanner Bacon</h1>
      <h3>Mission 12</h3>
      <p>This is a React app meant to demonstrate functions and components.</p>
      <p>To do this, here is a list of all the NCAA teams participating in March Madness:</p>
    </div>
  );
}

class Team extends React.Component<{
  school: string;
  name: string;
  city: string;
  state: string;
}>{
  render() {
    const { school, name, city, state } = this.props;

    return (
      <div>
        <h2>Name: {school}</h2>
        <h3>Location: {city}, {state}</h3>
        <p>Mascot: {name}</p>
        <br />
      </div>
    );
  }
}

function TeamList() {
  return (
    <div>
      {allTeams.map((team: Team) => (
        <Team {...team} key={team.school} />
      ))}
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <NameHeader />
        <TeamList />
      </header>
    </div>
  );
}

export default App;
